mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT T .*, s.DISPLAY_NAME FROM ACCOUNT_MANAGER_PARAMETER T LEFT JOIN SYS_USER s ON T .USER_ID = s. ID WHERE 1 = 1
AND s.DISPLAY_NAME=#{displayName}
ORDER BY t.CREATED_TIME DESC
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT COUNT (*) FROM (SELECT T .*, s.DISPLAY_NAME FROM ACCOUNT_MANAGER_PARAMETER T INNER JOIN SYS_USER s ON T .USER_ID = s. ID WHERE 1 = 1
AND s.DISPLAY_NAME=#{displayName}
ORDER BY t.CREATED_TIME DESC)

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select * from( select b.display_name, a.base_pay, a.id, (select org.name from sys_organization org where id in (select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.id))) as instcode from account_manager_parameter a, sys_user b where a.user_id = b.id) where 1=1
AND DISPLAY_NAME=#{displayName}
AND instcode=#{organName}
order by instcode
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT COUNT (*) FROM ( select * from ( select b.display_name, a.base_pay, (select org.name from sys_organization org where id in (select t.org_id from sys_department t where t.id in (select tt.dept_id from sys_dept_user tt where tt.user_id = b.id))) as instcode from account_manager_parameter a, sys_user b where a.user_id = b.id) where 1=1
AND DISPLAY_NAME=#{displayName}
AND instcode=#{organName}
order by instcode )
